import React from 'react';
import './App.css';



class WitForm extends React.Component {
	constructor(props) {
    super(props);
    this.state = {'request': ''};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
  	let val = event.target.value;
  	this.setState({'request': val});
  }

  handleSubmit(event) {
    event.preventDefault();
    alert('You submitted: ' + this.state.request);
    fetch('https://example.com/profile/avatar', {
	  method: 'PUT',
	  body: {'body': this.state.request}
	})
	.then(response => response.json())
	.then(result => {
	  console.log('Success:', result);
	})
	.catch(error => {
	  console.error('Error:', error);
	});
  }

  render () {
  	return (
	    <div className="App">
	      <form onSubmit={this.handleSubmit}>
	      	<label>
		        How can we help you?
		        <input type="text" placeholder="What years was the bmw 3 series sold" onChange={this.handleChange}/>
	      	</label>
	      	<input type="submit" value="Submit" />
	      </form>
	    </div>
	  );
  }
}

export default WitForm;
